    public static String fileToByteHex(Context context, boolean insetByte, boolean addFlag, int resRawId) {
        try {
            InputStream is = context.getResources().openRawResource(resRawId);
            int value = -1;
            StringBuilder sbHex = new StringBuilder();
            while ((value = is.read()) != -1) {
                if (addFlag) {
                    if (sbHex.length() > 0) {
                        sbHex.append(",");
                    }
                }
                if (insetByte) {
                    sbHex.append("(byte)");
                }
                sbHex.append(String.format("0x%02X", (byte) value));
            }
            is.close();
            String result = sbHex.toString();
            return result;
        } catch (IOException e) {
            e.printStackTrace();
        }
        return null;
    }


